/**
 * While webgl's clip space has coordinates [-1, 1] (left to right), [-1, 1] (bottom to top),
 * textures go from [0, 1] (left to right), [0, 1] (bottom to top).
 */
export interface ShapeA {
    vertices: [number, number, number][];
    texturePositions: [number, number][];
}
export interface ShapeE {
    vertices: [number, number, number][];
    texturePositions: [number, number][];
    vertexIndices: [number, number, number][];
}
export declare const triangleA: (width: number, height: number) => ShapeA;
export declare const triangleE: (width: number, height: number) => ShapeE;
export declare const rectangleA: (width: number, height: number) => ShapeA;
export declare const rectangleE: (width: number, height: number) => ShapeE;
